                    anchor: new google.maps.Point(0, 0) // anchor
                };
                const marker = new googleMap.maps.Marker({
                    position: latlng,
                    icon: icon,
                    map: map
                });
                let riesgo = ''

                if (rcv == "1") {
                    riesgo = 'RCV';
